crawlers.script:
let contents = [];

KYNERIX
    .begin()
    .forEach('dd div.meta', doc => {
        // Iterate over docs
        contents.push(
            {
                type: "paper",
                dataset: "arxiv",
                // Use selectors to extract content fields from page
                title:   KYNERIX.selectText("div.list-title", doc),
                authors: KYNERIX.selectText("div.list-authors", doc),
                subject: KYNERIX.selectText("div.list-subjects", doc)
            }
        )
    })
    .forEach('dt span a[href^="/pdf"', (dt, index) => {
        // Retrieve PDF urls
        contents[index].pdfURL = dt.href;

        // Creates content in data set
        // and schedules a subsequent script task to load and extract the PDF content

        KYNERIX.addContent(contents[index]).withScriptTask("arxiv-load-pdf");
    })
    .assertContentCount(1, 100)
    .end();

scripts.script:
/*
   Example of how to run a query against a data set, and render the results by using a template
*/ 

let dataset = KYNERIX.context("dataset", "arxiv");  // Get dataset from context, or default value

await KYNERIX.queryDataset(dataset)                 // Run a query against the dataset
    .withType("paper")                              // Iterate over contents of type paper
    .withStatus("Summarized")                       // Only those that have been summarized
    .searchAll(
        // Retrieve all contents in a array - alternative: forEach()
        (papers) => {

            KYNERIX.addContent(
                {
                    key: "arxiv-summary.html",      // Use key as the file name
                    type: "report",                 // Optional, set a content type
                    dataset: dataset,               
                    html: KYNERIX.renderTemplate(   // Renders the template
                        "arxiv-template-report",
                        {                           // Build template context with the retrieved papers
                            papers: papers
                        })
                }
            );
        }
    );

/* 
Retrieve generated content with:

curl -s -X GET "http://<your kynerix url>/contents/arxiv/download?content-field=html&path-field=key&type=report" \
    --header "Content-Type: application/json" \
    --header "Authorization: <your api key>" \
    --output arxiv.zip
*/

KYNERIX.end();

let MAX_PAGES = KYNERIX.parameter("max-pdf-pages", 2);

let content = KYNERIX.parameter("content",
    {   // This content is just for testing
        "key": "168",
        "title": "Active Reasoning in an Open-World Environment",
        "foundTime": "2023-11-06T15:47:33.230+00:00",
        "dataset": "arxiv",
        "authors": "Manjie Xu, Guangyuan Jiang, Wei Liang, Chi Zhang, Yixin Zhu",
        "subject": "Subjects: Artificial Intelligence (cs.AI); Computer Vision and Pattern Recognition (cs.CV)",
        "pdfURL": "https://arxiv.org/pdf/2311.02018"
    });

KYNERIX.parsePDF(content.pdfURL, MAX_PAGES, (text) => {

    content.body = text;
    content.status = "PDF Loaded";

    KYNERIX.addContent(content).withPromptTask("arxiv-summarize-pdf").end();
})
